mapper.update:
UPDATE logans_field 
SET id = #{id}

        , task_id = #{taskId}


        , field_key = #{fieldKey}


        , field_remark = #{fieldRemark}

WHERE id = #{id}

mapper.select:
SELECT id, task_id taskId, field_key fieldKey, field_remark fieldRemark
FROM logans_field
WHERE id = #{id}
