mapper.update:
UPDATE logans_field2_enum 
SET id = #{id}

        , task_id = #{taskId}


        , file_id = #{fileId}


        , enum_value = #{enumValue}


        , allow_regex = #{allowRegex}

WHERE id = #{id}

mapper.select:
SELECT id, task_id taskId, file_id fileId, enum_value enumValue, allow_regex allowRegex
FROM logans_field2_enum
WHERE id = #{id}

SELECT id, task_id taskId, file_id fileId, enum_value enumValue, allow_regex allowRegex FROM logans_field2_enum WHERE 1 = 1
AND task_id = #{taskId}
AND file_id = #{fileId}
AND enum_value LIKE CONCAT('%', #{enumValue}, '%')
AND allow_regex LIKE CONCAT('%', #{allowRegex}, '%')
ORDER BY id DESC
