mapper.update:
UPDATE logans_task 
SET id = #{id}

        , task_name = #{taskName}


        , module_name = #{moduleName}


        , req_method = #{reqMethod}


        , req_uri = #{reqUri}


        , ans_type = #{ansType}


        , log_source = #{logSource}


        , ans_rate_type = #{ansRateType}


        , out_queue = #{outQueue}


        , unique_field = #{uniqueField}

WHERE id = #{id}

mapper.select:
SELECT id, task_name taskName, module_name moduleName, req_method reqMethod, req_uri reqUri, ans_type ansType, ans_rate_type ansRateType, out_queue outQueue, log_source logSource, unique_field uniqueField
FROM logans_task
WHERE id = #{id}
