mapper.update:
UPDATE logans_task_res 
SET id = #{id}

        , task_id = #{taskId}


        , begin_time = #{beginTime}


        , end_time = #{endTime}


        , error_code = #{errorCode}


        , error_msg = #{errorMsg}


        , res_json = #{resJson}

WHERE id = #{id}

mapper.select:
SELECT id, task_id taskId, begin_time beginTime, end_time endTime, error_code errorCode, error_msg errorMsg, res_json resJson
FROM logans_task_res
WHERE id = #{id}

SELECT id, task_id taskId, begin_time beginTime, end_time endTime, error_code errorCode, error_msg errorMsg, res_json resJson
FROM logans_task_res  
WHERE 1 = 1 

        AND task_id = #{taskId}


        AND begin_time = #{beginTime}


        AND end_time = #{endTime}


        AND error_code = #{errorCode}


        AND error_msg LIKE CONCAT('%', #{errorMsg}, '%')


        AND res_json LIKE CONCAT('%', #{resJson}, '%')

ORDER BY id DESC
